#!/usr/bin/env python3\nimport hashlib,sys\ndef main(argv):\n    if len(argv)!=2:\n        print(\"usage: hash_hg_fixture_ids.py\",file=sys.stderr); return 2\n    sid=hashlib.sha3_256(b\"HG_AVRO_v1\").hexdigest()\n    cid=hashlib.sha3_256(b\"HG_JSONLD_v1\").hexdigest()\n    print(\"schema_id=sha3-256:\"+sid)\n    print(\"context_id=sha3-256:\"+cid)\n    return 0\nif __name__==\"__main__\":\n    raise SystemExit(main(sys.argv))\n